void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //pixel
    vec2 xy = fragCoord.xy/iResolution.xy;

    //color
    vec4 lightGreen = vec4(0.5,0.78,0.66,0);
    lightGreen.r = xy.x;
    lightGreen.b = xy.y;
    lightGreen.g = xy.x - 0.1 / xy.y;

    vec4 mask = vec4(1,1,1,0);
    mask.r *= abs(sin(iTime/2.5- 0.5));
    mask.b *= abs(sin(iTime/3.0));
    mask.g *= abs(sin(iTime/2.0 + 0.5));

    if (xy.x > 0.4 && xy.x < 0.6 && xy.y > 0.3 && xy.y < 0.7) {
    lightGreen.r = (1.0 - xy.x);
    lightGreen.b = (1.0 - xy.y);
    lightGreen.g = 1.0 - (xy.x - 0.1 / xy.y);
    }

    if (xy.x > 0.5 && xy.x < 0.7 && xy.y > 0.4 && xy.y < 0.8) {
    mask.b *= abs(sin(iTime/2.5- 0.5));
    mask.g *= abs(sin(iTime/3.0));
    mask.r *= abs(sin(iTime/2.0 + 0.5));
    }



    //texture
    vec4 C1961 = texture(iChannel0, xy);
    C1961.r = xy.x;
    fragColor = lightGreen*mask;

    if (xy.x > 0.3 && xy.x < 0.5 && xy.y > 0.2 && xy.y < 0.6) {
    fragColor = (lightGreen*mask+C1961) / 2.0;
    }
}
