////////////////////////////////////////////////////////////////////////////////
//
// RLE Earth. mla, 2022
//
// modified by jolle to use variably 1-2 bytes per span, with lengths up to 256
//
////////////////////////////////////////////////////////////////////////////////
// Fill the window with an irregularly sized image from buffer.
////////////////////////////////////////////////////////////////////////////////

const float WIDTH = 512.0;
const float HEIGHT = 256.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord/iResolution.xy;
  if (iMouse.z > 0.0) uv -= (iMouse.xy-0.5*iResolution.xy)/iResolution.xy;
  uv.y = 1.0-uv.y;
  if (WIDTH <= iResolution.x) uv.x += 0.05*iTime;
  uv = fract(uv);
  uv.x *= WIDTH/max(WIDTH,iResolution.x);
  uv.y *= HEIGHT/max(HEIGHT,iResolution.y);
  vec3 col = texture(iChannel0,uv).rgb;
  col = pow(col,vec3(0.6));
  fragColor = vec4(col,1);
}
