int n = 1000;
float period = 1.5;
float prop_speed = 0.1;

vec2 rand(vec2 co){
    return vec2(
        pow((fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453)-0.5)*2.,1.),
        pow((fract(cos(dot(co.yx ,vec2(8.64947,45.097))) * 43758.5453)-0.5)*2.,1.)
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    precision highp float;
    float time = iTime;

    vec2 uv = fragCoord/iResolution.xy;
    float min_dist = 10.;
    vec4 tex;

    for (int i = 0; i < n; i++) {
        vec2 co = rand(vec2(i,i));
        float dist = distance(co,uv);
        if (dist<min_dist){
            min_dist = dist;
            tex = texture(iChannel0,co);
        }

    }

    if (min_dist > prop_speed*(time/period - floor(time/period))) {
        tex = vec4(0.,0.,0.,0.);
    }

    // Output to screen
    fragColor = vec4(tex);
}
