void mainImage(out vec4 O, vec2 I)   
{
    vec4 i = modf(vec4(I+=iTime*1e2,32e3-I)/32.,O);
    O += dot(i*i.yzwx, fract(sin(vec4(93,92,0,1) + O.x+92.*O.y)*7e4) )-O;
}


