void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy);
}

void mainImage(out vec4 O, vec2 u)
{
    float a=.05;
    mat2 m = mat2(cos(a),-sin(a),sin(a),cos(a));
    vec2 R = iResolution.xy,
         U = u - R/2.,
         uv = U * m  / R + .5;
    vec4 col = iFrame<2 ? vec4(0,0,1,1) : texture(iChannel0, uv) - .1 / 256.;
    U.x += sin( float(iFrame)*.15 ) *R.x/4.;
    col = length(U+vec2(0.,R.x/8.)) < R.x/60. ? vec4(1,1,0,1) : col;
    col = length(U) < R.x/60. ? vec4(1,0,0,1) : col;
    O = col;
}
