float function(float x, float y) {
    return (pow(x, 2.0)*2.0 + pow(y, 2.0) - 4.0)*
           pow(pow(x, 2.0) + pow(y+0.75, 2.0)*10.0 - 0.5, 0.7)*
           pow(pow(x+0.5, 2.0) + pow(y-0.75, 2.0) - 0.1, 0.6)*
           pow(pow(x-0.5, 2.0) + pow(y-0.75, 2.0) - 0.1, 0.6);
}

float axes(float x, float y) {
    if (abs(x) < 0.01 || abs(y) < 0.01)
        return 2.0;
    return 0.0;
}

float g_xy(float x, float y) {
    float g_x = pow(cos(x * 3.14159265), 64.0);
    float g_y = pow(cos(y * 3.14159265), 64.0);
    return max(g_x, g_y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float max_width = max(iResolution.x, iResolution.y);
    float aspect = iResolution.y / iResolution.x;

    vec2 coords = fragCoord / vec2(max_width, max_width);
    float x = coords.x - 0.5;
    float y = coords.y - 0.5 * aspect;

    x *= 10.0; y *= 10.0;

    float grid = max(axes(x, y), g_xy(x, y)) / 4.0;

    float value = function(x, y);
    value = 1.0 / value;

    fragColor = vec4(0.0, grid, abs(value), 0.0);
}
