#define PI 3.1415926535897932384626433832795

float gauss(float x) {
    return exp(-x * x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 dstCtr = (iMouse.xy / iResolution.xy - vec2(0.5, 0.5));

    vec2 dst = (iMouse.xy / iResolution.xy - uv);
    dst.x = -dst.x;

    dst = (iMouse.xy / iResolution.xy - uv - 1.2 * dstCtr * length(dst));
    dst.x = -dst.x;

    float px = acos(dst.x / length(dst)) / PI;

    float prepx = px;

    if (dst.y > 0.) px = 2. - px;
    px = px / 2.;

    float color;

    float freq = iTime * 0.3;

    float superPo;

    if (floor(freq) == freq)
        superPo = sin(px * 2. * PI * freq + iTime * 3.20);
    else
        superPo =
            sin(px * 2. * PI * floor(freq) + iTime * 3.20) * (1. - freq + floor(freq))
            + sin(px * 2. * PI * ceil(freq) + iTime * 3.20) * (freq - floor(freq));



    color = gauss((5. + 0.2 * sin(iTime * 11.0)) * (1. + superPo * 0.3 - length(dst) * 6.0));

    //color = abs(1. + superPo * 0.3 - length(dst) * 5.0) < 0.1 ? 1. : 0.;

    vec2 realColor = vec2(prepx, (1. - prepx));

    realColor = color * normalize(realColor);

    fragColor = vec4(realColor.x, 0, realColor.y, 1);
}
