const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;
const float PI = 3.14159;
const float TWO_PI = 3.14159 * 2.0;
const vec3 matteWhite = vec3(0.85, 0.81, 0.78);
const vec3 matteRed = vec3(0.63, 0.065, 0.05);
