// Box with side lengths b
float cube(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// SquarePlane SDF
float plane(vec3 p, vec4 n)
{
    n = normalize(n);
    return dot(p, n.xyz) + n.w;
}

// Sphere with radius r at center c
float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

//describing a box
float box( vec3 pos, vec3 center, vec3 size, float corner )
{
    return length( max( abs( pos-center )-size, 0.0 ) )-corner;
}


#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define FLOOR 3
#define SPHERE1 4
#define SHORT_CUBE 5
#define BIG_CUBE 6
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 10.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 4.0))
#define SPHERE1_SDF sphere(pos, 2.0, vec3(0, 0.5, -4))
#define SHORT_CUBE_SDF cube(pos + vec3(0, 0.5, -4), vec3(2.2, 3, 2.2))
#define CUBE_SDF box(pos + vec3(0.0, (sin(iTime) * 2.0) - 5.0, 4.0), vec3(0.0), vec3(3.0), .0 )

