#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float distTorus(vec3 p, vec2 r) {
	float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float distBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float distCapsule(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    return length(p-c)-r;
}

float Blend( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float GetDist(vec3 p) {

    float t = iTime;

    float spher = length(p-vec3(1,0,cos(t)))-.5;
    float capsule = distCapsule(p, vec3(0), vec3(cos(t)), .2);  
    float torus = distTorus(p-vec3(sin(t),cos(t),0), vec2(1.8, .4));
    float box = distBox(p-vec3(1.+sin(t)), vec3(1,.5,2));

    float blend = Blend(capsule, spher, .1);
    blend = Blend(blend, torus, .4);
    blend = Blend(blend, box, .6);

    return blend;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = abs( GetDist(p) );
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 8, 0);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    return clamp(dot(n, l)*.5+.5, 0., 1.);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -8);
    ro.yz *= Rot(-m.y+.4);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = R(uv, ro, vec3(0,0,0), 1.5);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;

    	float dif = GetLight(p);
    	col = vec3(dif);
    }

    fragColor = vec4(col,1.0);
}
