vec3 rainbowColor(float t) {
    return .5 + .5 * cos(6.283 * (t + vec3(0., .33, .67)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;


     float l = length(uv);
     float a = atan(uv.x, uv.y) / (3.14156 * 2.0) - iTime;


     vec3 circle1 = mix(vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),smoothstep(0.5,0.49,l));

     vec3 circle2 = mix(vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),smoothstep(0.39,0.4,l));
      a = fract(a);
     vec3 col = mix(vec3(0.0,0.0,0.0),rainbowColor(a), (circle1 * circle2).r * a);



    // Output to screen
    fragColor = vec4(col,0.0);
}
