void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    uv.y += sin(uv.x*3.0 + iTime) * 0.1;

    /*if(uv.y > 0.5)
        fragColor = vec4(0, 0, 1, 1);
    else
        fragColor = vec4(0, 0, 0, 1);
    */
    float p = 1.0 / iResolution.y;// * mix(1.0, 10.0, 2.0 * abs(sin(iTime)));
    fragColor = mix(vec4(0, 0, 0, 1), vec4(0, 0, 1, 1), smoothstep(0.5 - p, 0.5 + p, uv.y));

}
