float plot(vec2 uv, vec2 ff){
return smoothstep(uv.y-0.2,uv.y,ff.x)-smoothstep(uv.y,uv.y+0.2,ff.x);
}

vec3 colort(float i){
vec3 mno = vec3(1.,1.,1.);
if (int(i)%7==0){mno = vec3(1.,1.,0.);}
return mno;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.-vec2(1.);
    uv.y *=iResolution.y/iResolution.x;

    uv = uv*mix(1.,0.1,length(uv)/2.2);
    vec3 col =vec3(0.);
    float t = fract(iTime*1./10.);
    float off = 0.;

     for(float r = 0.; r< 200.; r = r+1.){
     float l = length(abs(uv));
     float circle = smoothstep(l-0.009,l,off-abs(1.*t-1.))-smoothstep(l,l+0.025,off-abs(1.*t-1.));//those values substracted from off 
     //float circle = smoothstep(l-0.009,l,off-abs(fract(iTime)-t))-smoothstep(l,l+0.025,off-abs(fract(iTime)-t));
     vec3 mno = colort(r);                                                                         //are totally "magical" and are a subject     
     col += vec3(circle)*mno;                                                                      // for experiments :)
     off += 0.02;
     }

    col = col * smoothstep(0.1,0.45,length(uv)/2.2);
    fragColor = vec4(col,1.0);
}
