void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -=0.5;

     uv.x *= iResolution.x/iResolution.y;

    float time = iTime;


    float d = length(uv);
	float c = d;
    float r = 0.6;

    r = abs(sin(r * time));

    c = smoothstep(r, r-0.02, d) - smoothstep(r,r - 0.06, d);

    fragColor = vec4(vec3(c), 1.0);

}
