vec3 c_red = vec3(1.0,0.0,0.0);
vec3 c_blue = vec3(0.0,0.0,1.0);

float circle(vec2 st,vec2 center,float radius) { 
  float blur = 0.002;
  float border_width = 0.02;

  float pct = distance(st,center);

  float radius2 = radius-border_width;

  float big_circle = smoothstep(radius,radius+blur,pct);
  float small_circle = smoothstep(radius2,radius2+blur,pct);
  return 1.0-abs(small_circle-big_circle);
}  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5,0.5);


    float pct = circle(st,center,0.2);
    vec3 color = mix(c_blue,c_red,pct);

    fragColor = vec4(color,1.0);
}
