void mainImage( out vec4 O, in vec2 SC )
{
    vec4 spline      = TEXF0(SC);
    vec4 knot        = TEXF1(SC);
    vec4 convex_hull = TEXF2(SC);

    float min = MIN3(spline.r, knot.r, convex_hull.r);

    O = 
        min == 1.0 ?
            vec4(1) :                            // Background color
            min == spline.r ?
                vec4(0, spline.r, spline.r, 1) : // Spline color
                min == knot.r ?
                    vec4(0, knot.r, 0, 1) :      // Knot color
                    vec4(convex_hull);           // Convex hull  color
}

#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define INITIALLY_FAR               9e30
#define TEXF0(SC)                 ( texelFetch(iChannel0, ivec2(SC), 0) )
#define TEXF1(SC)                 ( texelFetch(iChannel1, ivec2(SC), 0) )
#define TEXF2(SC)                 ( texelFetch(iChannel2, ivec2(SC), 0) )
#define MIN3(a, b, c)             ( min(min((a), (b)), (c)) )
#define NMY                       ( iMouse.y / R.y )                 // in range [0.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )

#define RANDOM

/* 2 knots for linear,
   3 knots for quadratic,
   4 knots for cubic,
   5 knots for quartic,
   6 knots for quintic,
   e.t.c. */
const int num_of_ctrls = 6; 
vec2 ctrls[num_of_ctrls] = vec2[num_of_ctrls](
        /* x - interpolation parameter,
           y - data
        x values must be in increasing order,
        y in any order. */
		vec2(-1.5,  0.0),
		vec2(-0.9,  0.0),
		vec2(-0.3,  0.0),
		vec2(0.3,   0.0),
		vec2(0.9,   0.0),
        vec2(1.5,   0.0)
);

/* Functions for quadratic and cubic Largange interpolation. *
vec2 quadraticLagrangeInterpolation(vec2[3] ctrls, float x)
{
    float L02 = 
        ((x - ctrls[1].x)          * (x - ctrls[2].x)) /
        ((ctrls[0].x - ctrls[1].x) * (ctrls[0].x - ctrls[2].x));
    float L12 = 
        ((x - ctrls[0].x)          * (x - ctrls[2].x)) /
        ((ctrls[1].x - knots[0].x) * (ctrls[1].x - ctrls[2].x));
    float L22 = 
        ((x - ctrls[0].x)          * (x - ctrls[1].x)) /
        ((ctrls[2].x - ctrls[0].x) * (ctrls[2].x - ctrls[1].x));

    return vec2(
        x,
        L02 * ctrls[0].y + L12 * ctrls[1].y + L22 * ctrls[2].y);
}

vec2 cubicLagrangeInterpolation(vec2[4] ctrls, float x)
{
    float L03 = 
        ((x - ctrls[1].x)          * (x - ctrls[2].x)          * (x - ctrls[3].x)) /
        ((ctrls[0].x - ctrls[1].x) * (ctrls[0].x - ctrls[2].x) * (ctrls[0].x - ctrls[3].x));
    float L13 = 
        ((x - ctrls[0].x)          * (x - ctrls[2].x)          * (x - ctrls[3].x)) /
        ((ctrls[1].x - ctrls[0].x) * (ctrls[1].x - ctrls[2].x) * (ctrls[1].x - ctrls[3].x));
    float L23 = 
        ((x - ctrls[0].x)          * (x - ctrls[1].x)          * (x - ctrls[3].x)) /
        ((ctrls[2].x - ctrls[0].x) * (ctrls[2].x - ctrls[1].x) * (ctrls[2].x - ctrls[3].x));
    float L33 = 
        ((x - ctrls[0].x)          * (x - ctrls[1].x)          * (x - ctrls[2].x)) /
        ((ctrls[3].x - ctrls[0].x) * (ctrls[3].x - ctrls[1].x) * (ctrls[3].x - ctrls[2].x));

    return vec2(
        x,
        L03 * ctrls[0].y + L13 * ctrls[1].y + L23 * ctrls[2].y + L33 * ctrls[3].y);
}
/**/

float circleSQSDF(
    in vec2 S,           // sample coordinates
    in vec2 C,           // circle center
    in float r,          // circle radius
    in float smoothness) // AA parameter
{
    // Sample coordinates relative to circle center.
    float _x = S.x - C.x; float _y = S.y - C.y;

    // Signed distance of areas (squared distancies).
    float sds = _x * _x + _y *_y - r * r;

    float ss = smoothness * smoothness;

    // Smoothstep interpolation.
    return smoothstep(-ss, ss, sds);
    //return smoothstep(-ss, ss, sds * sds); // outline
}

/* 
    Returns random vec2([-1.0; 1.0], [-1.0; 1.0]).
*/
vec2 vector2HashUI32(
    const uint x,
    const uint y)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint entropy0 = 123u;
    const uint entropy1 = 456u;
    const uint entropy2 = 789u;

    // Use linear offset method to mix coordinates.
    uint value0 = y * entropy2 + x;
    uint value1 = x * entropy2 + y;

    // Calculate hash.
	value0 += entropy1; value0 *= 445593459u; value0 ^= entropy0;
    value1 += entropy1; value1 *= 445593459u; value1 ^= entropy0;

    // 2.0f / 4294967295.0f = 4.6566128730773926e-10

    return 
        vec2(
            float(value0 * value0 * value0) * 4.6566128730773926e-10f - 1.0f,
            float(value1 * value1 * value1) * 4.6566128730773926e-10f - 1.0f);
}

void randomize_ctrls(float time)
{
    uint T = uint(time);
    for (int k = 0; k < num_of_ctrls; ++k)
    {
        ctrls[k] = vector2HashUI32(T + uint(k), 2u * T + uint(k * 2));
    }
}

void randomize_ctrls2(float time)
{
    time *= 0.5;
    float t = fract(time);
    uint T = uint(time);

    for (int k = 0; k < num_of_ctrls; ++k)
    {
        uint x = T + uint(k);
        uint y = 2u * T + uint(k * 2);
        vec2 c = vector2HashUI32(x, y);
        vec2 n = vector2HashUI32(x + 1u, y + 2u);

        ctrls[k] += (c * (1. - t) + n * t) * vec2(0.3, 0.5);
    }
}

float weight(float x, int idx)
{
    float product = 1.0;

    for (int k = 0; k < num_of_ctrls; ++k)
    {
        product *= 
            k == idx ?
                1.0 :
                x - ctrls[k].x;
    }

    return product;
}

vec2 LagrangeInterpolation(float x)
{
    float sum = 0.0;

    for (int s = 0; s < num_of_ctrls; ++s)
    {
        float ratio = 
            weight(x, s) /
            weight(ctrls[s].x, s); // this weights can be precalculated

        sum += ctrls[s].y * ratio;
    }

    return vec2(x, sum);
}

vec2 find_closest_curve_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    float xMin = ctrls[0].x;
    float xMax = ctrls[num_of_ctrls - 1].x;
    float dx = (xMax - xMin) * 0.005;


    for (float x = xMin; x <= xMax; x += dx)
    {
        vec2 P = LagrangeInterpolation(x);

        vec2 diff = P - NDC;
        float sqDist = dot(diff, diff);

        minSqDist = min(minSqDist, sqDist);

        closestP = 
            minSqDist == sqDist ? 
                P :
                closestP;
    }

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_curve_point_to_pixel(ctrls, NDC),
        NDC,
        0.01,
        0.011));
}

vec2 find_closest_control_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    for (int k = 0; k < num_of_ctrls; ++k)
    {
        vec2 diff = ctrls[k] - NDC;
        float sqDist = dot(diff, diff);

        minSqDist = min(minSqDist, sqDist);

        closestP = 
            minSqDist == sqDist ? 
                ctrls[k] :
                closestP;
    }

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_control_point_to_pixel(ctrls, NDC),
        NDC,
        0.02,
        0.018));
}

vec2 find_closest_convex_hull_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    int num_of_lines = num_of_ctrls - 1; // 2 knots per line

    for (int l = 0; l < num_of_lines; ++l)
    {
        for (float t = 0.; t <= 1.; t += 0.1)
        {
            vec2 P = ctrls[l] * (1. - t) + ctrls[l + 1] * t;

            vec2 diff = P - NDC;
            float sqDist = dot(diff, diff);

            minSqDist = min(minSqDist, sqDist);

            closestP = 
                minSqDist == sqDist ? 
                    P :
                    closestP;
        }
    }

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_convex_hull_point_to_pixel(ctrls, NDC),
        NDC,
        0.008,
        0.0088));
}
