bool circle(vec2 uv,vec2 center, float size)
{
    return length(uv-center)>size;
}
bool donut(vec2 uv, float width, float girth,float offset)
{
    bool o = true;
    for (int i = 0; i < 22; i++)
    {
        float time = iTime+offset;
        float turn = float(i)/22.0*3.1415*2.0;
        o = o^^circle(uv,width*vec2(sin(turn+time*1.1243),cos(turn+time*1.456)),girth);
    }
    return o;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pix_l = 1.0/vec2(iResolution.x, iResolution.y);
    vec2 uv = gl_FragCoord.xy*pix_l;
    uv.x *= iResolution.x/ iResolution.y;

    uv -= vec2(iResolution.x/ iResolution.y/2.0,.5);

    bool o = true;
    for (int i = 0; i < 8; i++)
    {
        float fi = float(i)*.6;
        fi = exp(fi);
        o = o^^donut(uv,.3/fi,0.06/fi,float(i));
    }

    fragColor = vec4(vec3(o),1.0);
}
