#define PI 3.14159265359

mat2 rotate2D(float rad){
    return mat2(cos(rad), -sin(rad), sin(rad), cos(rad));
}

mat2 tarnslate(vec2 t){
    return mat2(t.x, 0., t.y, 0.);
}

float fillRect(float size, vec2 st){
    float margin = (1.-size)/2.;
    vec2 b = smoothstep(4.5/iResolution.y, 0., abs(st-.5) - size/2. );
    return b.x*b.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st *= 3.0;
    float row = ceil(st.y);
    float column = ceil(st.x);
    st = fract(st);

    st -= vec2(0.5);
    st = st*rotate2D(2.*PI/8.+ PI/2.*smoothstep(-1., 1., sin(iTime+0.3*row-0.1*column)*2.));
    st += vec2(0.5);

    vec3 col;
    col = vec3(.9, .5, abs(sin(iTime+0.3*row-0.1*column)));
    col = vec3(fillRect(sqrt(2.)/2., st))*col;

    // Output to screen
    fragColor = vec4(col,1.0);
}
