// EXACT distance to a pyramid. This shader computes the exact euclidean
// distances (not a bound based on half spaces). This allows to do
// operations on the shape such as rounding (see https://iquilezles.org/articles/distfunctions)
// while other implementations don't. Unfortunately the maths require us to do
// one square rooat sometimes to get the exact distance.

// List of other 3D SDFs: https://www.shadertoy.com/playlist/43cXRl
//
// and https://iquilezles.org/articles/distfunctions


// signed distance to a pyramid of base wxw and height h


float dot2(in vec2 v ) { return dot(v,v); }

float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);
    p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y<0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}



float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

vec3 Rx( in vec3 p, in float degree)
{
    float theta = radians(degree);
    mat3 m = mat3(1.0, 0.0, 0.0, 
              0.0, cos(theta), -sin(theta),
              0.0, sin(theta), cos(theta));
    return m*p;
}


vec3 Ry( in vec3 p, in float degree)
{
    float theta = radians(degree);
    mat3 m = mat3(cos(theta), 0.0, sin(theta),
              0.0, 1.0, 0.0,
              -sin(theta), 0.0, cos(theta));
    return m*p;
}


vec3 Rz( in vec3 p, in float degree)
{
    float theta = radians(degree);
    mat3 m = mat3(cos(theta), -sin(theta), 0.0, 
              sin(theta), cos(theta) , 0.0,
              0.0, 0.0, 1.0);
    return m*p;
}

float sdPyramid( in vec3 pos, in float w, in float d, in float h )
{
    float sdPyramidExtXY = opExtrussion(pos, 
        sdTrapezoid(pos.xy, w, w / 2.0, h), d);

    //vec3 pos2 = Ry(pos, 90.0);
    vec3 pos2 = pos.zyx;
    float sdPyramidExtZY = opExtrussion(pos2, 
        sdTrapezoid(pos2.xy, d, d / 2.0, h), w);
    return max(sdPyramidExtXY, sdPyramidExtZY);
}

float map( in vec3 pos )
{
    float h = 1.0*(0.5+0.45*sin(iTime*1.0));
    float d = 1.0*(0.5+0.45*cos(iTime*1.0));
    float w = 1.0*(0.5+0.45*sin(iTime*2.0));

    return sdPyramid(pos, w, d, h);

}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 3
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 3.0*cos(an), sin(an*2.0), 3.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.5, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 30.0;
        float t = 0.0;
        for( int i=0; i<1024; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}
