float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 rotateCell(float seed, vec2 st){
    if(seed<0.25){
        st = vec2(1.-st.x, st.y);
    }else if(seed<0.5){
        st = vec2(st.x, 1.-st.y);
    }else if(seed<0.75){
        st = vec2(1.-st.x, 1.-st.y);
    }
    return st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.y * 10.;

    vec2 rc = floor(st); //row and column

    st = fract(st);
    st = rotateCell(random(rc), st);

    float col;

    //1
    col = smoothstep(15./iResolution.y, 0., abs(st.x-st.y));

    //2
    col = step(length(st),0.6)-step(length(st),0.4);
    col += step(length(vec2(1.)-st),0.6)-step(length(vec2(1.)-st),0.4);

    //3
    col = step(length(st),0.6);
    col += step(length(vec2(1.)-st),0.6);

    fragColor = vec4(vec3(col),1.0);
}
