#define BLACK_COL vec3(32,43,51)/255.
#define WHITE_COL vec3(235,241,245)/255.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;   

    float c = texture(iChannel0, uv).r;

    float fc = smoothstep(.2, 0.19, c);

    vec3 col = mix(BLACK_COL, WHITE_COL, fc);    

    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord.xy / iResolution.xy;       
    float pc = texture(iChannel0, ouv).r * 1.15;

    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.x; 

    float t = iTime*5.;        

    vec2 mf = vec2(cos(t), sin(2. * t) / 3.5);

    float d = length(uv + mf*.3);

    float s = smoothstep(.05, 0.0, d)*10.;

    fragColor = mix(vec4(s), vec4(pc), .8);
}
