void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor.rgb = 
    vec3(
         mix(
         //sample color
              texture(iChannel0, uv).rgb, 
         //dot product of the luminosity generic value and the Color
              vec3(
                  dot(
                      vec3(0.200, 0.500, 0.100), 
                      texture(iChannel0, uv).rgb)
                     ), 

              //mix factor 
              fract(iTime)));


}
