void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cellSize = 32.0;

    float value = floor(fragCoord.x / cellSize) + floor(fragCoord.y / cellSize);
    value = fract(value * 0.5);
    value *= 2.0;

    vec3 col = vec3(value, value, value);
    fragColor = vec4(col,1.0);
}
