#define pi 3.1416
void mainImage( out vec4 rgba, in vec2 h2o )
{
    rgba=vec4(0,0,0,1);
    vec2 R=iResolution.xy;
    h2o=h2o-R/2.;
    vec2 snow=vec2(
        length(h2o),
        atan(h2o.y,h2o.x)
    );vec2 opts=vec2(pi/12.,pi/24.);
    snow.y=mod(snow.y,opts.x);
    if(snow.y>=opts.y)snow.y=abs(snow.y-opts.x);
    snow.y+=floor(snow.x)*sin(4.*pi-(iTime)/360./4.);
    if(snow.x<=R.y/2.){
        rgba=vec4(cos(snow.x)*cos(snow.x))+1.;
        if(sin(cos(snow.x/2.))<(sin(degrees(snow.y/2.)))){
            rgba=vec4(pi/48.);
    }}else rgba=vec4(0,pi/24.,pi/12.,1);
}
