void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = textureLod(iChannel0, uv, 0.0f).rrr;

    fragColor = vec4(1.0f - color,1.0);
}

#define GOLDEN_RATIO (1.61803398874989484820)
#define dilation (GOLDEN_RATIO * 9999.0)
#define size 1024
#define scale (iTime * 2.0f * float(size))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0f * fragCoord / iResolution.xy - 1.0f;

    vec2 offset = vec2(0);
    offset.x = float(iFrame & (size-1));
    offset.y = float((iFrame - size) & (size-1));

    vec3 bn = textureLod(iChannel0, (fragCoord + offset)/float(scale), 0.0f).rgb;
    bn.xy = 2.0f * bn.xy - 1.0f;
    bn.z *= dilation * bn.x * bn.y;

    offset.x = float((iFrame - 1) & (size-1));
    offset.y = float((iFrame - size - 1) & (size-1));

    vec3 bn_last = textureLod(iChannel0, (fragCoord + offset)/float(scale), 0.0f).rgb;
    bn_last.xy = 2.0f * bn_last.xy - 1.0f;
    bn_last.z *= dilation * bn_last.x * bn_last.y;

    offset.x = float((iFrame + 1) & (size-1));
    offset.y = float((iFrame - size + 1) & (size-1));

    vec3 bn_next = textureLod(iChannel0, (fragCoord + offset)/float(scale), 0.0f).rgb;
    bn_next.xy = 2.0f * bn_next.xy - 1.0f;
    bn_next.z *= dilation * bn_next.x * bn_next.y;

    vec3 last = textureLod(iChannel1, fragCoord/iResolution.xy, 0.0f).gba;
    last.xy = 2.0f * last.xy - 1.0f;
    last.z *= dilation * last.x * last.y;

    float last_last = textureLod(iChannel1, (fragCoord - vec2(1))/iResolution.xy, 0.0f).a;
    float last_next = textureLod(iChannel1, (fragCoord + vec2(1))/iResolution.xy, 0.0f).a;

    float depth = bn.z * last_next / last_last;

    vec3 p = vec3(uv, depth);
    float d = distance(p, bn), dd = 0.0f;

    vec3 best = bn;

    dd = distance(p, bn_last);
    best = mix(best, bn_last, bvec3(dd < d));
    d = min(d, dd);

    dd = distance(p, last);
    best = mix(best, last, bvec3(dd < d));
    d = min(d, dd);

    dd = distance(p, bn_next);
    best = mix(best, bn_next, bvec3(dd < d));
    d = min(d, dd);


    best.xy = best.xy * 0.5f + 0.5f;
    best.z /= dilation * best.x * best.y;


    fragColor = vec4(d,best);
}
