// variant of https://shadertoy.com/view/fl3yD4
// variant of https://shadertoy.com/view/7ldcWn

// --- version with correct order + shake


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,  
         U = 2.*( u+u - R ) / R.y, V,D;
    O += .5-O;                                //  3-grey variant
    float a, z = 9., s,d;
    for( int k=0; k<9; k++ )
        D = vec2(k%3,k/3),
        V = fract(U) + D -1.,  
        V += .02*cos( iTime*30. +vec2(0,11)+ 6.3* fract(1e4*sin(dot(floor(U-D),17.-R))) ),
        a = sin ( 4.* atan(V.y,V.x) ),
        s = smoothstep( 12./R.y, 0., abs(d = length(V)-.85) -.07 ),
        a < z && s > .5 ?
            O *= 1.-s, O += s * ( .5 + .5* cos(30.*d) - O )
          , z=a  : z; 
}
