// See 'Common' tab for tweakable knobs

void mainImage( out vec4 fragColor, in vec2 uv )
{
    uv = (uv - iResolution.xy/2.0)/iResolution.y;
    uv = inverse(mat2(
        1.0, 0.5, 
        -1.0, 0.5
    ))*uv;
    uv = 24.0*uv + 32.0;

    vec2 uv2 = uv;
    #if METHOD == 4
        uv2 = floor(uv+0.5) + smoothstep(0.0, 1.0, fract(uv+0.5)) - 0.5;
    #endif

    float t = texture(iChannel0, uv2/iResolution.xy).r;
    #if METHOD == 1
        t = clamp((t-0.5)*1.5 + 0.5, 0.0, 1.0);
    #elif METHOD == 2
        t = smoothstep(0.0, 1.0, t);
    #elif METHOD == 3
        t = smoothstep(0.2, 0.8, t);
    #endif

    #if USE_TEXTURE
        fragColor = degamma(texture(iChannel1, uv/8.0));
    #else
        fragColor = vec4(1.0);    
    #endif

    fragColor.rgb *= t;    
    fragColor = gamma(fragColor);
}

void mainImage( out vec4 fragColor, in vec2 uv )
{
    uv -= vec2(32.0,32.0);

    #if SEMIFOG
    float t = length(uv - vec2(6.0,0.0)) < 8.0 ? 1.0 :
        length(uv - vec2(-6.0,2.0)) < 5.0 ? 0.5 : 0.0;
    #else
    float t = length(uv - vec2(6.0,0.0)) < 8.0 ? 1.0 :
        length(uv - vec2(-6.0,2.0)) < 5.0 ? 1.0 : 0.0;
    #endif    

    fragColor = degamma(vec4(t,t,t,1.0));
}

/*
METHOD:
    0 - simple bilinear
    1 - linear compression
    2 - smoothstep
    3 - tighter smoothstep
    4 - domain warp
*/
#define METHOD 0

// enables partial fog; works properly only with METHOD == 0 or 4
#define SEMIFOG 0

// enable/disable gamma aware blending
#define USE_GAMMA 1

// enable/disable textured ground
#define USE_TEXTURE 1


#if USE_GAMMA
vec4 gamma(vec4 v) { return vec4(pow(v.rgb, vec3(0.4545)), v.a); }
vec4 degamma(vec4 v) { return vec4(pow(v.rgb, vec3(1.0/0.4545)), v.a); }
#else
vec4 gamma(vec4 v) { return v; }
vec4 degamma(vec4 v) { return v; }
#endif
