float GetTexture(float x, float y)
{
    return texture(iChannel0, vec2(x, y)).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float threshold = 0.5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float x = uv.x;
    float y = uv.y;

      float xValue = -GetTexture(x, y) -1.0*GetTexture(x-0.110, y) - GetTexture(x-0.010, y+0.010)
        + GetTexture(x+0.01, y-1.0) + 1.0*GetTexture(x+0.010, y) + GetTexture(x+0.010, y+0.010);

    float yValue = GetTexture(x-0.010, y-0.010) + 1.0*GetTexture(x, y-0.010) + GetTexture(x+0.010, y-0.010)
        - GetTexture(x-0.010, y+0.010) - 1.0*GetTexture(x, y+0.010) - GetTexture(x+0.010, y+0.010);


    if(length((vec2(xValue, yValue))) > threshold){

        fragColor = vec4(0);

    }
    else{
         vec2 uv = vec2(x, y);
      vec4 currentPixel = texture(iChannel0, uv);

      vec4 empty = vec4(1);

      fragColor = empty;

    }


}
