float lum(in vec3 rgb) {
    return dot(rgb, rgb) / 3.0;
}

void mainImage(out vec4 RGBA, in vec2 XY) {
    vec2 UV = XY/iResolution.xy;

    float l0 = lum(texture(iChannel0, UV).rgb);
    float l1 = lum(texture(iChannel1, UV).rgb);

    vec2 uv = vec2(l0, l1);

//  uv = iResolution.x/100.* vec2(dFdx(l0),dFdy(l0));

    uv = mix(uv, UV, cos(iTime * 0.2) * 0.1 + 0.9);

    RGBA = texture(iChannel2, uv);
}
