// Fork of "Focus shift" by Daniil. https://shadertoy.com/view/XdX3D7
// 2022-05-07 03:17:29

float perspective = 0.3;
float duration = 1.;
float stength = 7.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
	float focus = sin(iTime / duration)*.35+.5;
	float blur = stength*sqrt(abs(0.5 - focus));

	/* perpective version */	
    vec2 p2 = vec2(p.x-(1.-p.y)*perspective*(p.x*2. - 1.), -p.y);

	/* simple vesion */
	//vec2 p2 = -p;	

	fragColor = texture(iChannel0, p2, blur);
}
