void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col;

    // Output to screen
    float pi = 3.1415926;
    float omega = 2. * pi * 640. * (0.5 + 0.5 * sin(iTime * 2. * pi / 640. / 2.));
    float v = 0.5 + 0.5 * sin( omega * (uv.x - 0.5) );
    if (uv.y > v){
        col = vec3(0.905,0.022,0.696);
    }
    else {
        col = vec3(0.486,0.020,0.800);
    }

    fragColor = vec4(col,1.0);
}
