#define PI  3.14159265359
#define TAU 6.28318530717

///Inigo Quilez Signed Distance Field
//https://iquilezles.org/articles/distfunctions2d
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
float Disk(vec2 uv, vec2 center, float radius, float blur) 
{
    float sdfCircle = length(uv-center)-radius;
	return clamp(sdfCircle * (10.0/blur),0.0,1.0);
}

///UV
vec2 CenterUV(vec2 fragCoord)
{
    ///Centering Operation
	vec2 uv = vec2( fragCoord.xy - 0.5*iResolution.xy );
	// [0, iResolution.x] -> [-0.5*iResolution.x, 0.5*iResolution.x]
	// [0, iResolution.y] -> [-0.5*iResolution.y, 0.5*iResolution.y]
    return uv;
}
vec2 AspectRatioUV(vec2 fragCoord)
{	
    vec2 uv = CenterUV(fragCoord.xy);
	uv = 2.0 * uv.xy / iResolution.y;
    // AspectRatio = 1920/1080 = 1.778
    // [-iResolution.x/2, iResolution.x/2] ->[-1.778, 1.778]
	// [-iResolution.y/2, iResolution.y/2] ->[-1, 1]
    return uv;
}

///COLOR
vec3 HSVcolor(vec3 RGB)
{
    vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(RGB.xxx + K.xyz) * 6.0 - K.www);
    return RGB.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), RGB.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV manipulation
    vec2 uv = AspectRatioUV(fragCoord.xy);
    uv *= 1.2;

    //Parameter
    float time = iTime * .8;

    //Visu
    vec3 pix = vec3(uv,0);
    pix = vec3(0.1);
    for(int i = 0; i < 480; i++)
    {
        float t = time + abs(sin(float(i)/4800.0* TAU + time));
        vec2 pos = vec2(cos( float(i)/32.0 * TAU + t),sin(float(i)/32.0* TAU+t)) * sin(float(i)/6.0* TAU+t)
        + vec2(0.0,0.0);
        float circleMask = Disk(uv,pos, 0.08, .33);
        pix = mix(HSVcolor(vec3(float(i)/1600.0, 0.95,0.95)), pix, circleMask); 
    }

	fragColor = vec4(pix, 1.0);
}
