//use mouse
//use up and down arrow keys

bool keyPressed(int keyCode) {
	return bool(texture(iChannel0, vec2((float(keyCode) + 0.5) / 256., .5/3.)).r);   
}

float circle(vec2 uv, vec2 pos, float r, float blur){
    vec2 a = pow(uv-pos, vec2(2.));
    return smoothstep(r+blur, r-blur, sqrt(a.x+a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 R = iResolution.xy;
    vec3 col = vec3(0.);

    if(keyPressed(38)){
        col += vec3(.5);
    }
    if(keyPressed(40)){
        col -= vec3(.5);
    }
    col += vec3(iMouse.x/R.x-.5);

    col += circle(fragCoord, vec2(0.), 10., 1.)/2.;
    col += circle(fragCoord, vec2(iMouse.x, 0.), 10., 1.)/2.;
    col += circle(fragCoord, vec2(0., iMouse.y), 10., 1.)/2.;
    col += circle(fragCoord, iMouse.xy, 10., 1.)/2.;
    col += circle(fragCoord, iMouse.xy/2., 10., 1.);

    fragColor = vec4(col,1.0);
}
