// https://www.shadertoy.com/view/llcXRl
// Texture: https://www.shadertoy.com/media/a/08b42b43ae9d3c0605da11d0eac86618ea888e62cdd9518ee8b9097488b31560.png

vec4 SampleFontTex(vec2 uv)
{
    vec2 fl = floor(uv + 0.5);
    uv = fl + fract(uv+0.5)-0.5;
    // Sample the font texture. Make sure to not use mipmaps.
    // Add a small amount to the distance field to prevent a strange bug on some gpus. Slightly mysterious. :(
    // -16 is bias to use largest mipmap
    return texture(iChannel0, (uv+0.5)*(1.0/16.0), -16.0) + vec4(0.0, 0.0, 0.0, 0.000000001);
}

void renderChar(int x,int y, vec2 pos, float size, vec3 color, inout vec4 fragColor, vec2 fragCoord){
    vec2 p = (fragCoord - pos) / size;
    if(abs(p.x) < 0.5 && abs(p.y) < 0.5){
        float po = SampleFontTex(p+vec2(float(x),float(y))).a;
        if(abs(po-0.5) < 0.005){
            fragColor.xyz = mix(fragColor.xyz,color,smoothstep(0.505,0.495,po));
        }else if(po < 0.5){
            fragColor.xyz = color;
        }
    }
}

#define renderC(x,y,p,s,c) renderChar(x,y,p,s,c,fragColor,fragCoord)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(cos((fragCoord/iResolution.xy).xyxx))*0.5;


    vec2 tpos = vec2(100,300);
    #define CC(xp,yp) renderC(xp,yp,tpos,40.,vec3(0,1,0));tpos.x += 20.
    CC(3,10); CC(4,11); CC(6,11); CC(0,5); CC(6,11); CC(15,9); CC(14,9); CC(4,8);
    #undef CC

    tpos = vec2(100,200);
    #define CC(xp,yp) renderC(xp,yp,tpos,90.,vec3(0,1,1));tpos.x += 45.
    CC(3,10); CC(4,11); CC(6,11); CC(0,5); CC(6,11); CC(15,9); CC(14,9); CC(4,8);
    #undef CC

    tpos = vec2(100,100);
    #define CC(xp,yp) renderC(xp,yp,tpos,180.,vec3(1,1,1));tpos.x += 90.
    CC(3,10); CC(4,11); CC(6,11); CC(0,5); CC(6,11); CC(15,9); CC(14,9); CC(4,8);
    #undef CC
}
