// Fork of "Supah Draw Circle" by supah. https://shadertoy.com/view/NtyGWW
// 2021-11-23 10:09:35

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float cursor = clamp(texture(iChannel0, uv).r, 0., 1.);
    vec3 tex1 = texture(iChannel1, uv).rgb;

    uv -= .5;
    uv *= .9 + sin(uv.x * 30. + iTime) * .04;
    uv += .5;
    vec3 tex2 = vec3(texture(iChannel1, uv).rgb - vec3(1.1, .5, .4));

    vec3 col = mix(tex1, tex2, cursor);

    fragColor = vec4(col,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    float c = 1. - smoothstep(0., 0.3, length(uv - m));
    c -= sin(uv.y + .03 * 4.) + sin(iTime * 0.4 + uv.x * 10. * cos(uv.y * 3.)) * 0.02;
    vec3 col = vec3(c);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb * 0.9 + texture(iChannel1, uv).rgb * 0.96;
    fragColor = vec4(col,.9);
}
