void mainImage( out vec4 c, in vec2 o )
{
    o.y-=iResolution.y/2.0;o.x=float(int(o.x));
    vec4 ox=vec4((mod(o.x+float(iFrame),11.0)-3.0),(mod(o.x+float(iFrame),13.0)-5.0),(mod(o.x+float(iFrame),17.0)-7.0),1)/2.0;
    o.x+=float(iFrame);
    vec2 old=o;
    o.x=(mod(floor(o.x),2.0));
    o.y=(old.y)+floor((sin(float(int(floor(old.x))/4)-iTime)*85.0));
    c=vec4(0.0);
    vec4 color=1.0-vec4(sin(float(int(floor(old.x))/4)-iTime));
    float r = (o.x*o.x+o.y*o.y);
    vec2 oo=old;
    old.x-=1.0;
    oo.x=(mod(floor(oo.x+float(iFrame)),2.0));
    oo.y=(old.y)+floor((sin(float(int(floor(old.x+1.0))/4)-iTime+3.1416)*85.0));
    float r2 = oo.x*oo.x+oo.y*oo.y;
    vec4 color2=1.0-vec4(sin(float(int(floor(old.x+1.0))/4)-iTime+3.1416));
    if((sqrt(r))<=2.0) c=color*color2+ox;
    else if((sqrt(r2))<=2.0) c=color2*color+ox;
    //if(c.x>0.0){c+=0.75;c/=2.0;}
}
