renderpass.code:
float pi = 3.14159;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Setup
    vec2 uv = fragCoord/iResolution.xy;
    float res = iResolution.x / iResolution.y+.1;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse /= 3.;
    mouse = vec2(mouse.x * res, mouse.y) + vec2(.5);
    uv += mouse-0.5;
    uv -= vec2(.6);

    uv -= vec2(sin(iTime)*.15, cos(iTime)/6.);
    uv = vec2(uv.x*res, uv.y);
    float rho = .25/length(uv);
    float pho = atan(uv.x/uv.y) / pi;
    pho += (sin(iTime)*.2);
    pho = mod(pho, 1.);
    rho += iTime*.2;

    // Time varying pixel color
    vec3 col = vec3(texture(iChannel0, vec2(pho, rho)));
    col *= (length(uv)*1.25);
    col += .295/length(uv) * vec3(.25, .3, .1);

    // Output to screen
    fragColor = vec4(col,rho);
}
