// Fork of "Movement in Squares B. Riley" by CuttyFlamingo. https://shadertoy.com/view/Nly3Ry
// 2021-11-25 14:39:02

float bridgetRiley(float x){
    x = abs(x);
    if(x>758.31643){
        return (0.00625 * x) + 10.20625;
    }
    float a = 180.7672452*(0.00885609212-(0.06165085810/(x+6.974615261)));
    float xx = x*x,x3 = xx*x,x4 = x3*x,x5 = x4*x;
    return a + (0.0616508581*x) - (0.00016333738093*xx) + (0.0000002626423947 * x3)-(0.00000000021798673228*x4)+(0.000000000000072135039846*x5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) - vec2(0.6447368421,0.6447368421);
    uv.y*=(iResolution.y/iResolution.x);

    float Colour = floor(mod(floor(mod(bridgetRiley(1900.0*uv.x),2.0))+bridgetRiley(1900.0*uv.y),2.0));
    // Time varying pixel color
    vec3 col = vec3(Colour,Colour,Colour);

    // Output to screen
    fragColor = vec4(col,1.0);
}
