float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = vec2(iMouse.xy / iResolution.xy);

    // mess with these to adjust pixelization sizes
    float minRange = 16.0 * mouse.x;
    float maxRange = 128.0 * mouse.y;

    vec2 lumaCoords = floor(uv * maxRange) / maxRange;

    vec3 rgb = texture(iChannel0, lumaCoords).rgb;

    float luma = luma(rgb);


    // brighter has more detail
    luma = mix(0.01, 1.0, luma);

    // darker has more detail
    //luma = mix(1.0, 0.01, luma);

    vec2 target = vec2(luma );

    float area = maxRange * 2.0 * mouse.x;
    float dist = distance(floor(uv*area)/area, target);

    dist += 0.25;
    dist = max(0.0, min(1.0, 1.0 - dist));

    dist = dist * dist;

    float details = minRange + floor(dist * maxRange);

    // uncomment for more details.
    //details = pow(2.0, details);

    uv = floor(uv * details) / details;

    vec4 color = texture(iChannel0, uv);

	fragColor = color;
}
