void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float cB = 0.0; 
    float cG = 0.0;
    float cR = 0.0;
    float modR = 0.9;
    float modG = 0.76;
    float modB = 0.4;
    float stepadd1 = 4.923;
    float stepadd2 = -11.72;

    float maxbars=27.;
    float barsize=0.4;


    float sinus = sin(iTime*1.1+uv.y*stepadd1)*2.3+sin(iTime*2.9+uv.y*stepadd2)*2.3-4.6;
    float samplepoint=0.0;

    float revsinus = -9.2 -sinus;
    float delta = (revsinus-sinus)/(maxbars-1.);  


    for (float licz=1. ; licz<=maxbars ; ++licz){

    samplepoint = sinus+uv.x*10.;
    if ((samplepoint > 0.) && (samplepoint < barsize)) {

    cR=sin((samplepoint)*3.2/barsize)*modR;
    cG=sin((samplepoint)*3.2/barsize)*modG;
    cB=sin((samplepoint)*3.2/barsize)*modB;
    }

    sinus=sinus+delta;
    }


    // Output to screen
    fragColor = vec4(cR,cG,cB,1.0);
}
