void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy / iResolution.xx;
    vec4 frag = texture(iChannel0, uv);

    // primeira linha
    frag = mix(frag, vec4(1.),
        step(.999, (sin(uv.x-uv.y-iTime*3.))));

    // segunda linha
    frag = mix(frag, vec4(1.),
        step(.97, (sin(uv.x-uv.y-(2,4.+iTime)*3.))));

    fragColor = frag;
}
