void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 RGBcolor = vec3(0.0,0.0,0.0);
    vec3 RGBmod   = vec3(0.0,0.0,0.0);

    float step1   =  0.093;
    float step2   = -0.063;
    float barsize =  0.8; //8;

    float maxbars=141.-(140.*uv.y);

    for (float licz=1. ; licz<=maxbars ; ++licz){
    float samplepoint = sin(iTime*1.1+step1*licz)*2.3+sin(iTime*2.3+step2*licz)*2.3-4.6+uv.x*10.0;

    if (abs(samplepoint-barsize/2.)< barsize/2.) RGBcolor = RGBmod*sin((samplepoint)*3.2/barsize);

    RGBmod += vec3(0.02,0.01,0.08);

    }

    // Output to screen
    fragColor = vec4(RGBcolor,1.0);
}
