float Shadow(float time, vec2 uv)
{
    float speedx=1.0;
    float freqx=2.0;
    float speedy=1.0;
    float freqy=2.0;

    float timeshiftx=(time*speedx)+(uv.x*freqx);
    float timeshifty=(time*speedy)+(uv.y*freqx);

    float shadow=1.0+cos(timeshiftx)*sin(timeshifty)*0.3;

    return shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float x= length(vec2(uv.x*1.7,uv.y) - vec2(0.85, 0.5));

    float wiggle =sin(iTime*2.0+(uv.x*5.0))*0.01;
    float wi=wiggle*0.01;

    fragColor = vec4(0.7,0.1,0,1)*Shadow(iTime,uv);

    if(uv.y>0.25+wiggle){
          fragColor = vec4(0,0,0.4,0)*Shadow(iTime,uv);
    }

    if(uv.y>0.75+wiggle){
          fragColor = vec4(0.7,0.1,0,1)*Shadow(iTime,uv);
    }
    if(x<0.2+wiggle){
        fragColor = vec4(1,1,1,1)*Shadow(iTime,uv);
    }

}
