void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy * 0.5f)/iResolution.x;

    float shag = 40.0f;
    // Time varying pixel color
    vec3 col = vec3(
        0.30 + 0.7*cos(-iTime*30.4 + length(uv)*shag + 0.00000000000 - atan(uv.x, uv.y)), 
        0.30 + 0.7*cos(-iTime*14.5 + length(uv)*shag + 4.18879020479 - atan(uv.x, uv.y)), 
        0.30 + 0.7*cos(-iTime*20.0 + length(uv)*shag + 2.09439510239 - atan(uv.x, uv.y))
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
