// 150 chars - Further golfing by Jedi Master Fabrice!
void mainImage(out vec4 o, vec2 u)
{ 
    vec2 r = iResolution.xy,
         s = 1e2*( .3 + iMouse.yy/r.y ) / r.y;    
    mat2 m = mat2(1,-1,1,1);
    o = texture(iChannel0, m*ceil(.5*u*m*s)/s/r);
}

