// see common tab for infos

#define EPSILON 0.001
#define DIST_MAX 1000.0

// https://iquilezles.org/articles/distfunctions/

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float halfspace(vec3 p)
{
    return p.z;
}

float sdSphere( vec3 p, float s )
{
    return length(p) - s;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float udLine( vec3 p, vec3 a, vec3 b)
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

struct result
{
    float dist;
    uint id;
};

//result union(result a, result b) // ERROR: 'union' : Illegal use of reserved word
result combine(result a, result b)
{
    //return a.dist < b.dist ? a : b; // ERROR: '? : '
    if(a.dist < b.dist) return a;
    return b;
}

float plane(vec3 p)
{
    //return udTriangle(p, vec3(-0.5,-1,0), vec3(+0.5,-1,0), vec3(0,+1,0)) - 0.1;
    float d = udTriangle(p, vec3(0,-1,1.0), vec3(0,-1,0), vec3(0,+1,0));
    d = min(d, udTriangle(p, vec3(-1,-1,-0.5), vec3(0,-1,0), vec3(0,+1,0)));
    d = min(d, udTriangle(p, vec3(+1,-1,-0.5), vec3(0,-1,0), vec3(0,+1,0)));
    return d - 0.025;
}

result map(vec3 p, state s)
{
    //result r = result(plane(rotate(s.orientation, p - s.position)), 0u);
    result r = result(plane(rotate(p - s.position, s.orientation)), 0u);
    //r = combine(r, result(udLine(p, s.position, waypoints[s.waypoint]) - 0.01, 0u));
    for(uint i = 0u; i < uint(waypoints.length()/* length should have return type uint - arrays can't have negative amount of elements */); i++)
    {
        r = combine(r, result(sdSphere(p - waypoints[i], 1.0), i + 1u));
    }
    //r = combine(r, result(sdBox(p - waypoints[s.waypoint], vec3(1.0)), 0u)); // mark current waypoint
    r = combine(r, result(sdBoxFrame(p - waypoints[s.waypoint], vec3(1.0), 0.05),~0u)); // mark current waypoint
    float dist = distance(s.position, waypoints[s.waypoint]);
    vec3 project = s.position + dist * rotate(s.orientation, vec3(0,1,0)/*local forward*/); // project tangent of trajectory
    r = combine(r, result(sdSphere(p - project, 0.2), ~0u));
    if(texelFetch( iChannel1, ivec2(71/*'G'*/,2),0).x >= 0.5)
        r = combine(r, result(halfspace(p + vec3(0,0,50)), 0u)); // ground

    return r;
}

// modified https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p, state s) 
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map(p + k.xyy*h, s).dist + 
                      k.yyx*map(p + k.yyx*h, s).dist + 
                      k.yxy*map(p + k.yxy*h, s).dist + 
                      k.xxx*map(p + k.xxx*h, s).dist);
}

result trace(vec3 ro, vec3 rd, float t0, float t1, state s)
{
    for(float t = t0; t < t1;)
    {
        result h = map(ro + rd * t, s);
        if(h.dist < EPSILON)
            return result(t, h.id);
        t += h.dist;
    }
    return result(t1, 0u);
}

#define pi 3.1415926

vec3 palette(float t)
{
    return cos(2.0 * pi * (t + vec3(0,1,2)/3.0)) * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    state s = decode(iChannel0);
    //s.position = vec3(0.5,0,0);

    vec2 p = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    p.x *= float(iResolution.x) / float(iResolution.y);

    vec3 ro = vec3(0.0, 0.0,-40.0);
    vec3 rd = normalize(vec3(0.5 * p.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact

    bool localview = (iMouse.x > 10.0 || iMouse.y > 10.0) ? (texelFetch( iChannel1, ivec2(32/*SPACE*/,2),0 ).x >= 0.5) : (cos(2.0 * pi * iTime / 10.0) < 0.0);
    if(localview) // lock-on to plane position
    {
        if(localview) // lock-on to plane rotation
        {
            rd.xy = -rd.xy; // workaround - messed up the rotation somewhere
            mat2 Pitch = mat2(vec2(0.0,1.0), vec2(-1.0, 0.0));
            rd.yz = Pitch * rd.yz;
            rd = rotate(s.orientation, -rd);
            ro = s.position + rotate(s.orientation, vec3(0,-5,0));
        }
        else
        {
            mat2 Pitch = mat2(vec2(0.0,1.0), vec2(-1.0, 0.0));
            rd.yz = Pitch * rd.yz;
            ro = s.position + vec3(0,5,0);
        }
    }
    else
    {
        float mx = 2.0 * pi * float(iMouse.x) / float(iResolution.x);
        float my = pi  + pi * float(-iMouse.y) / float(iResolution.y);
        mx = (iMouse.x > 10.0) ? mx : 2.0 * pi * fract(iTime * 0.01);
        my = (iMouse.y > 10.0) ? my : pi / 2.0;

        mat2 Yaw = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
        //mat2 Pitch = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
        mat2 Pitch = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
        ro.yz = Pitch * ro.yz;
        ro.xy = Yaw * ro.xy;
        rd.yz = Pitch * rd.yz;
        rd.xy = Yaw * rd.xy;
    }

    result r = trace(ro, rd, 0.0, DIST_MAX, s);

    vec3 color = vec3(0);
    if(r.dist < DIST_MAX)
    {
        if(r.id != ~0u)
        {
            vec3 dst = ro + rd * r.dist;
            vec3 n = normal(dst, s);

            vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
            vec3 ambient = vec3(0.05);
            float brightness = max(dot(lightdir, n), 0.0);
            float specular = pow(max(0.0, dot(normalize(rd + lightdir), n)), 50.0);
            color = (r.id == 0u) ? vec3(s.approach ? 1.0 : 0.5) : palette(float(r.id) / float(waypoints.length()));
            color = (ambient + brightness) * color;
            color += specular;
        }
        else
        {
            color = pow(mix(vec3(0,0.25,0),vec3(0.75,1,0.75), vec3(cos(2.0 * pi * iTime) * 0.5 + 0.5)), vec3(2.0)); // mark green when distance decreasing
            if(!s.approach)
                //color.xy = color.yx; // mark red if distance increasing
                color.yz = color.zy; // mark blue if distance increasing
            bool combined = (texelFetch( iChannel1, ivec2(67/*'G'*/,2),0).x >= 0.5);
            if(combined)
                color.x = 1.0;
        }
    }

    fragColor = vec4(sqrt(color), 1.0); // approximate gamma
}

