// CONTROLS: SPACE toggles view, 'g' toggles ground, 'c' toggles combined control mode, '0'-'8' select waypoint manually - if possible
//           (Sometimes it appears a waypoint can't be selected because the conditions to advance to the next are immediately fulfilled). 

// NOTE: using vec4 here would be more concise - but when quaternions have their own type,
//       the type-checking done by the glsl compiler can be used to detect errors
//       caused by accidentally confusing vec4 with quaternions
//       (e.g. accidentally multiplying a quaternion with an axis-angle, both stored in vec4).
//       Also in mathematical notation usually the real part is written first, followed by the imaginary part:
//       w + xi + yj + zk while the vec4 constructor expects x, y, z, w.
//       A minor convenience: vector-scalar notation simplifies accessing vector-part in rotation.
//       When defining a quaternion type the mathematical order can be chosen.
//       Last but not least using an own type for quaternions allows to overload functions, like norm
//       (this becomes increasingly valuable when introducing more systems,
//        like complex numbers, dual numbers, dual quaternions...)

// https://en.wikipedia.org/wiki/Quaternion
struct quat
{
    float s;
    vec3 v;
};

// NOTE: https://en.wikipedia.org/wiki/Quaternion#Quaternions_and_the_space_geometry
// NOTE: The cross-product of the vector-parts of any quaternions p, q can be expressed by (pq - qp)/2
//       (the scalar part of this expression always evaluates to zero)
//       (the expression pq - qp is apparently called "commutator")
// NOTE: The dot-product of all four components of quaternions p, q can be expressed by (pq* + qp*)/2
//       (where * denotes the quaternion conjugate, i.e. negation of the vector-part)
// NOTE: This is straight forward but cumbersome to check - use e.g. Maxima:
//       % quaternion-multiplication (in component form)
//       a_3(a_1, b_1, c_1, d_1, a_2, b_2, c_2, d_2) := a_1*a_2 - b_1*b_2 - c_1*c_2 - d_1*d_2;
//       b_3(a_1, b_1, c_1, d_1, a_2, b_2, c_2, d_2) := a_1*b_2 + b_1*a_2 + c_1*d_2 - d_1*c_2;
//       c_3(a_1, b_1, c_1, d_1, a_2, b_2, c_2, d_2) := a_1*c_2 - b_1*d_2 + c_1*a_2 + d_1*b_2;
//       d_3(a_1, b_1, c_1, d_1, a_2, b_2, c_2, d_2) :=a_1 * d_2 + b_1 * c_2 - c_1 * b_2 + d_1 * a_2;
//       % evaluate squared norm |q|^2 = qq* = a^2 + b^2 + c^2 + d^2
//       a_3(a,b,c,d,a,-b,-c,-d);b_3(a,b,c,d,a,-b,-c,-d);c_3(a,b,c,d,a,-b,-c,-d);d_3(a,b,c,d,a,-b,-c,-d);
//       % evaluate (pq - qp)
//       a_3(a,b,c,d,A,B,C,D)-a_3(A,B,C,D,a,b,c,d);b_3(a,b,c,d,A,B,C,D)-b_3(A,B,C,D,a,b,c,d);c_3(a,b,c,d,A,B,C,D)-c_3(A,B,C,D,a,b,c,d);d_3(a,b,c,d,A,B,C,D)-d_3(A,B,C,D,a,b,c,d);
//       % evaluate (pq* + qp*)
//       a_3(a,b,c,d,A,-B,-C,-D)+a_3(A,B,C,D,a,-b,-c,-d);b_3(a,b,c,d,A,-B,-C,-D)+b_3(A,B,C,D,a,-b,-c,-d);c_3(a,b,c,d,A,-B,-C,-D)+c_3(A,B,C,D,a,-b,-c,-d);d_3(a,b,c,d,A,-B,-C,-D)+d_3(A,B,C,D,a,-b,-c,-d);

quat quat_identity()
{
    return quat(1.0, vec3(0.0, 0.0, 0.0));
}

quat conjugate(quat q)
{
    return quat(q.s,-q.v);
}

float norm_squared(quat q)
{
    return q.s * q.s + dot(q.v, q.v);
}

float norm(quat q)
{
    return sqrt(norm_squared(q));
}

quat mul(float s, quat q)
{
    return quat(s * q.s, s * q.v);
}

quat mul(quat q, float s)
{
    return quat(s * q.s, s * q.v);
}

quat div(quat q, float s)
{
    return quat(q.s / s, q.v / s);
}

//quat normalize(quat q) // ERROR: "Name of a built-in function cannot be redeclared as function"
quat normalify(quat q) // NOTE: can't reuse function name normalize here
{
    return div(q, norm(q));
}

//quat normalize(quat q) // ERROR: "Name of a built-in function cannot be redeclared as function"
//quat inverse(quat q) // ERROR: "Name of a built-in function cannot be redeclared as function"
quat inv(quat q) // NOTE: can't reuse function name inverse here
{
    return div(conjugate(q), norm_squared(q));
}

quat div(float s, quat q)
{
    return mul(s, inv(q));
}

quat neg(quat q)
{
    return quat(-q.s,-q.v);
}

quat add(quat a, quat b)
{
    return quat(a.s + b.s, a.v + b.v);
}

quat sub(quat a, quat b)
{
    return quat(a.s - b.s, a.v - b.v);
}

quat mul(quat a, quat b)
{
    return quat(a.s * b.s - dot(a.v, b.v), a.s * b.v + b.s * a.v + cross(a.v, b.v));
}

quat div(quat a, quat b)
{
    return mul(a, inv(b));
}

vec3 rotate(quat q, vec3 v)
{
    return mul(mul(q, quat(0.0, v)), inv(q)).v;
}

// inverse rotation
vec3 rotate(vec3 v, quat q)
{
    return mul(mul(inv(q), quat(0.0, v)), q).v;
}

quat by_vec4(vec4 v)
{
    return quat(v.w, vec3(v));
}

vec4 to_vec4(quat q)
{
    return vec4(q.v, q.s);
}

#define dt (1.0/60.0)

vec3 waypoints[] =
    vec3[]
    (
        10.0 * vec3(0,0,0),
        10.0 * vec3(+1,+1,+1),
        10.0 * vec3(-1,+1,+1),
        10.0 * vec3(+1,-1,+1),
        10.0 * vec3(-1,-1,+1),
        10.0 * vec3(+1,+1,-1),
        10.0 * vec3(-1,+1,-1),
        10.0 * vec3(+1,-1,-1),
        10.0 * vec3(-1,-1,-1)
    );

struct state
{
    vec3 position;
    quat orientation;
    vec3 velocity;
    vec3 rotation;
    uint waypoint;
    bool approach;
};

//vec3 align(state s) // rotate towards current waypoint
//{
//    vec3 diff_global = waypoints[s.waypoint] - s.position;
//    vec3 diff_local = rotate(diff_global, s.orientation);
//    vec3 delta_rotation_local = 2.0 * cross(vec3(0, 1, 0)/*forward*/, diff_local);
//    vec3 delta_rotation_global = rotate(s.orientation, delta_rotation_local);
//    //delta_rotation_global += cross(vec3(0, 0,1)/*global up*/, rotate(vec3(0, 0, 1)/*local up*/, s.orientation)); // try to maintain "up"
//    return delta_rotation_global;
//}

vec3 align(state s, bool combined, bool maintainup) // rotate towards current waypoint
{
    vec3 diff_global = waypoints[s.waypoint] - s.position;
    if(length(diff_global) != 0.0)
    {
        diff_global = normalize(diff_global);
        vec3 diff_local = rotate(diff_global, s.orientation);
        vec3 sky_local = rotate(vec3(0, 0, 1)/*global up*/, s.orientation);
        vec3 delta_rotation_local = vec3(0);
        if(combined) // control all angles at once
        {
            delta_rotation_local += 20.0 * cross(vec3(0, 1, 0)/*local forward*/, diff_local);
            if(maintainup)
                delta_rotation_local += 10.0 * cross(vec3(0, 0, 1)/*local up*/, sky_local); // try to maintain "up"
        }
        else // control angles separately
        {
            delta_rotation_local.x += 20.0 * atan(diff_local.z, diff_local.y); // pitch
            delta_rotation_local.y += 20.0 * atan(diff_local.x, diff_local.z); // roll
            delta_rotation_local.z -= 20.0 * atan(diff_local.x, diff_local.y); // yaw

            if(maintainup)
                delta_rotation_local += 50.0 * cross(vec3(0, 0, 1)/*local up*/, sky_local); // try to maintain "up"
        }

        vec3 delta_rotation_global = rotate(s.orientation, delta_rotation_local);
        //delta_rotation_global += 20.0 * cross(vec3(0, 0,1)/*global up*/, rotate(vec3(0, 0, 1)/*local up*/, s.orientation)); // try to maintain "up"
        return delta_rotation_global;
    }

    return vec3(0);
}

bool can_lock(vec3 pos_diff, vec3 vel_diff)
{
    return dot(pos_diff, vel_diff) < 0.0;
}

// https://www.chrishecker.com/Rigid_Body_Dynamics "Rigid Body Dynamics" by Chris Hecker
// https://www.cs.cmu.edu/~baraff/sigcourse/notesd1.pdf "An Introduction to Physically Based Modeling: Rigid Body Simulation Iâ€”Unconstrained Rigid Body Dynamics" by David Baraff, Robotics Institute, Carnegie Mellon University
state update(state s) // rigid body
{
    s.position += s.velocity * dt;
    s.orientation = add(s.orientation, mul(mul(quat(0.0, s.rotation), s.orientation), dt * 0.5));
    s.orientation = normalify(s.orientation); // for stability

    return s;
}

state autopilot(state s, bool combined, bool maintainup)
{
    bool cl = can_lock(s.position - waypoints[s.waypoint], vec3(0) - s.velocity);
    float d0 = distance(s.position, waypoints[s.waypoint]);
    s = update(s);
    float d1 = distance(s.position, waypoints[s.waypoint]);
    if (d1 < d0) // distance to waypoint decreased
    {
        s.approach = true;
    }
    else if(d1 > d0) // distance to waypoint increased
    {
        if (s.approach && cl)
        {
            s.approach = false;
            s.waypoint++;
            if(s.waypoint >= uint(waypoints.length())) // workaround potential modulo bugs on windows
                s.waypoint = 0u;
        }
    }

    s.rotation = align(s, combined, maintainup) * dt; // rotate towards current waypoint

    float acceleration = 10.0;
    float damping = 5.0;
    s.velocity += rotate(s.orientation, vec3(0, acceleration, 0)) * dt;
    s.velocity += damping * (vec3(0) - s.velocity) * dt; // adjust to destination velocity

    return s;
}

#define POSITION ivec2(0,0)
#define ORIENTATION ivec2(1,0)
#define VELOCITY ivec2(2,0)
#define ROTATION ivec2(3,0)
#define WAYPOINT ivec2(4,0)
#define APPROACH ivec2(5,0)

state decode(sampler2D tex)
{
    state s;
    s.position = vec3(texelFetch(tex, POSITION, 0));
    s.orientation = by_vec4(texelFetch(tex, ORIENTATION, 0));
    s.velocity = vec3(texelFetch(tex, VELOCITY, 0));
    s.rotation = vec3(texelFetch(tex, ROTATION, 0));
    s.waypoint = uint(floor(texelFetch(tex, WAYPOINT, 0).x));
    s.approach = (texelFetch(tex, APPROACH, 0).x >= 0.5);
    return s;
}

// see common tab for infos

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,0.0,0.0);

    if(iFrame == 0)
    {
        if(ivec2(floor(fragCoord.xy)) == POSITION) fragColor = vec4(20.0,0.0,0.0,0.0);
        if(ivec2(floor(fragCoord.xy)) == ORIENTATION) fragColor = vec4(0.0,0.0,0.0,1.0);
        if(ivec2(floor(fragCoord.xy)) == VELOCITY) fragColor = vec4(0.0,0.0,0.0,0.0);
        if(ivec2(floor(fragCoord.xy)) == ROTATION) fragColor = vec4(0.0,0.0,0.0,0.0);
        if(ivec2(floor(fragCoord.xy)) == WAYPOINT) fragColor = vec4(0.0,0.0,0.0,0.0);
        if(ivec2(floor(fragCoord.xy)) == APPROACH) fragColor = vec4(0.0,0.0,0.0,0.0);
    }
    else
    {
        state s = decode(iChannel0);
        for(uint n = 0u; n < 10u; n++)
            if(texelFetch( iChannel1, ivec2(48u/*'0'*/+n,1),0).x >= 0.5 && n < uint(waypoints.length())) s.waypoint = n;
        bool combined = (texelFetch( iChannel1, ivec2(67/*'G'*/,2),0).x < 0.5);
        bool maintainup = (texelFetch( iChannel1, ivec2(71/*'G'*/,2),0).x >= 0.5);
        s = autopilot(s, combined, maintainup);
        if(ivec2(floor(fragCoord.xy)) == POSITION) fragColor = vec4(s.position, 0.0);
        if(ivec2(floor(fragCoord.xy)) == ORIENTATION) fragColor = to_vec4(s.orientation);
        if(ivec2(floor(fragCoord.xy)) == VELOCITY) fragColor = vec4(s.velocity, 0.0);
        if(ivec2(floor(fragCoord.xy)) == ROTATION) fragColor = vec4(s.rotation, 0.0);
        if(ivec2(floor(fragCoord.xy)) == WAYPOINT) fragColor = vec4(s.waypoint);
        if(ivec2(floor(fragCoord.xy)) == APPROACH) fragColor = vec4(s.approach ? 1.0 : 0.0);
    }
}
