}

float n31(vec3 s) {
    return fract(9473.482 * sin(dot(vec3(290.281, 757.389, 842.693), s)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y - .5;

    vec3 col = vec3(0);

    uv += normalize(uv) * smoothstep(0., .5, length(uv));
    //uv /= (sin(length(uv) * .2)*.5+.5);
    //uv.xy += sin(uv.xy * 100.) * .01;
    //uv = uv*(sin(iTime*.3)) * 2.;

    col.b += (1.-pow(max(0., cos(iTime * .2)), 10.)) * texture(iChannel0, uv).r - .5;

    float t = iTime*.6;
    float t2 = iTime*.3;
    col.b += sin(atan(uv.y, uv.x) * 1. + length(uv) * 8. - t);
    uv *= sin(col.b * .03);
    col.g += texture(iChannel0, uv).r * texture(iChannel0, sin(uv * 3.)).r;
    col.br = col.gb;

    float a = sin(t2) * .3 + 1.5;
    float dark = smoothstep(a, a+.5, length(col));
    float c = ((sin(t2)*.5+.5)-dark);
    col.r += c + c * dot(col, normalize(vec3(.1, .4, 1)));

    col.b += (1.5-a)*max(0., dot(col, normalize(vec3(0, 1, 1))));

    col = pow(col, vec3(1.2));

    fragColor = vec4(col,1.0);
}
