#define R iResolution.xy
#define T1(U) texture(iChannel0,U/R)

void mainImage( out vec4 C, in vec2 U )
{   

    float m = T1(U).w;//masse
    float ms = (sign(m -1.)+1.)*.5;
    float v = length(T1(U).xy)/10.;//velocity


    C = .5*m*vec4(1.,1.2,2.,1);

    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) C = vec4(0.5);
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) C = vec4(0.5);

}

#define dt .2
#define g .1
#define l .1
#define h 2
#define r 1.3

vec2 R;
vec4 Mouse;
float t;
int Frame;

#define T(p) texture(ch,(p)/R)

void pass( out vec4 Q, in sampler2D ch, in vec2 U )
{ 
    Q = vec4(0);
    vec4 dQ = vec4(0);
    float qw =T(U).w;
    for (int x = -h; x <= h; x++)
    for (int y = -h; y <= h; y++)
    {
        vec2 u = vec2(x,y);
        vec4 a = T(U+u);
        vec2 w1 = clamp(U+u+a.xy*dt-0.5*r,U - 0.5,U + 0.5),
             w2 = clamp(U+u+a.xy*dt+0.5*r,U - 0.5,U + 0.5);
        float m = (w2.x-w1.x)*(w2.y-w1.y)/(r*r);
        Q.xy += m*a.w*a.xy;
        Q.w += m*a.w;
        if(x!=0||y!=0)dQ.xy -= (qw<1.)?a.w*(a.w-1.)*u/dot(u,u):qw*qw*a.w*u/dot(u,u);       
    }
    if (Q.w>0.)
        Q.xy/=Q.w;
    Q.y -= dt*g;
    Q.xy += dt*l*dQ.xy;

    //Boundaries
    if (Frame < 1) Q = vec4(0,0,1,0);
    if (U.x < 1.||U.y < 1.) Q.xy *= 0.;
    if (R.x-U.x < 1.||R.y-U.y < 1.) Q.w *= 0.;
    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) Q.xy *= 0.;
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) Q.xy *= 0.;
 	if (length(U-vec2(0,0.9*R.y)) < .02*R.y) {Q.xy= Q.xy*.5+.5*vec2(.6+0.8*sin(1.5*t),0); Q.w = 1.;};

    //click mouse to add a circular obstacle
    if(length(Mouse.xy - U) < 20. && Mouse.z > 0.5)
        Q.xyw = vec3(0,0,1);

    Q = clamp(Q, vec4(-5,-5,0,0), vec4(5,5,0,15));

}

void mainImage( out vec4 Q, in vec2 U )
{      
    R = iResolution.xy; t = iTime; Mouse = iMouse; Frame = iFrame;
    pass( Q, iChannel0, U );

}

