// Display 🌵
Main 
{
    vec4 f = A(U);
    Q = 1.-(.8-0.5*(sin(3.5-.3*(f.z)+vec4(1,2,3,4))))*f.w;
}

// Forces 🦜
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        float f = 0.05*a.w*((a.w-1.)+.2);
        dQ.xy -= f*u;
    }
    Q += dQ;
    Q.y -= .5/R.y;
    vec2 M = 1.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    if(length(U-M)<.02*R.y)Q = vec4(.1*normalize(M-0.5*R),-1,1.);
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0);
    if (U.x<4.&&U.y>.8*R.y&&U.y<.9*R.y) Q.w = 1.,Q.z=10.*sin(.1*iTime);
    if (U.x<.05*R.x&&U.y<.8*R.y&&U.y>.7*R.y) Q.xy*=0.;
    if (U.x<.2*R.x&&U.x>.1*R.x&&U.y<.7*R.y&&U.y>.6*R.y) Q.xy*=0.;
    if (U.x<.7*R.x&&U.x>.3*R.x&&U.y<.6*R.y&&U.y>.55*R.y) Q.xy*=0.;
    if (U.x<.65*R.x&&U.x>.6*R.x&&U.y<.7*R.y&&U.y>.48*R.y) Q.xy*=0.;
    if (U.y<(.2-U.x/R.x*(.1+.1*sin((1.-U.x/R.x)*(1.-U.x/R.x)*50.)))*R.y) Q.xy *= 0.;
    if (U.x<1.||U.y<1.||R.y-U.y<1.) Q.xy*=0.;
    if(R.x-U.x<1.)Q *= 0.;
}

// Advect 🐿
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5+.5*smoothstep(.7,0.,Q.w*q.w);
       float k = .5+.5*smoothstep(.7,0.,Q.w*q.w);
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}

